import React, { useState } from 'react';
import { Announcement } from './components/Announcement';
import { CreateAnnouncer } from './components/CreateAnnouncer';

function App() {
  const [announcements, setAnnouncement] = useState([
    {
      id: '1',
      title: 'titfegefgefgefgefel',
      description: 'description',
      date: new Date(),
      isSelected: false,
    },
  ]);

  const addAnnouncements = (item) => {
    setAnnouncement([
      ...announcements,
      {
        id: Date.now(),
        title: item.title,
        description: item.description,
        date: new Date(),
        isSelected: false,
      },
    ]);
  };

  const saveAnnouncements = () => {};
  const deleteAnnouncements = (id) => {
    setAnnouncement(
      announcements.filter((announcement) => announcement.id !== id)
    );
  };
  const changeAnnouncements = () => {};

  return (
    <div className="container">
      <CreateAnnouncer
        announcements={announcements}
        addAnnouncements={addAnnouncements}
      />
      <Announcement
        announcements={announcements}
        saveAnnouncements={saveAnnouncements}
        deleteAnnouncements={deleteAnnouncements}
        changeAnnouncements={changeAnnouncements}
      />
    </div>
  );
}

export default App;
