import React, { useState } from 'react';
import Moment from 'react-moment';

export const AnnouncementItem = (props) => {
  const [isEdit, setEdit] = useState(false);
  console.log(props);
  const editHandler = () => {
    setEdit(true);
  };

  const editSection = () => {
    if (!isEdit) {
      return (
        <div className="row">
          <div className="col-10">
            <span>{props.announcement.title}</span>
          </div>
          <div className="col-2 ">
            <button
              className="btn btn-outline-danger mr-1"
              type="button"
              onClick={() => props.deleteAnnouncementHendler(props.id)}
            >
              <i className="fa fa-trash"></i>
            </button>
            <button
              className="btn btn-outline-warning"
              type="button"
              onClick={editHandler}
            >
              <i className="fa fa-pencil"></i>
            </button>
          </div>
        </div>
      );
    }
    return (
      <div className="row">
        <div className="col-10">
          <input
            type="text"
            className="form-control"
            placeholder="ToDo title"
            name="description"
            value={props.announcement.title}
            onChange={props.changeEditFormHandler()}
          />
          <input
            type="text"
            className="form-control"
            placeholder="ToDo description"
            name="titel"
            value={props.announcement.description}
            onChange={props.changeEditFormHandler()}
          />
        </div>
        <div className="col-2 ">
          <button
            className="btn btn-outline-danger mr-1"
            type="button"
            onClick={() =>
              props.deleteAnnouncementHendler(props.announcement.id)
            }
          >
            <i className="fa fa-trash"></i>
          </button>
          <button
            className="btn btn-outline-warning"
            type="button"
            onClick={() => props.saveEditHandler(props.announcement.id)}
          >
            <i class="fa fa-check-circle"></i>
          </button>
        </div>
      </div>
    );
  };

  return (
    <li
      className="list-group-item mt-1"
      onClick={() => props.selectHandler(props.announcement.id)}
    >
      {editSection()}
      <div>
        {props.isSelected && (
          <div className="row">
            <span>{props.announcement.description}</span>
            <div className="col-2">
              <Moment format="YYYY/MM/DD">{props.announcement.date}</Moment>
            </div>
          </div>
        )}
      </div>
    </li>
  );
};
