import React, { useState } from 'react';
import Moment from 'react-moment';

export const AnnouncementItem = (props) => {
  const [isEdit, setEdit] = useState(false);
  const editHandler = () => {
    setEdit((isEdit = true));
  };

  const editSection = () => {
    if (isEdit) {
      return (
        <label className="col-md-7 text-left">
          <form onSubmit={this.onSaveClick.bind(this)}>
            <input
              className="form-control input-sm"
              defaultValue={task}
              ref="editInput"
              type="text"
            />
          </form>
        </label>
      );
    }

    return (
      <label
        className="col-md-7 text-left text"
        style={taskStyle}
        onClick={this.props.toggleTask.bind(this, task)}
      >
        {task}
      </label>
    );
  };

  return (
    <li
      className="list-group-item mt-1"
      onClick={() => props.selectHandler(props.id)}
    >
      <div className="row">
        <div className="col-10">
          <span>{props.title}</span>
        </div>
        <div className="col-2 ">
          <button
            className="btn btn-outline-danger mr-1"
            type="button"
            onClick={() => props.deleteHandler(props.id)}
          >
            <i className="fa fa-trash"></i>
          </button>
          <button
            className="btn btn-outline-warning"
            type="button"
            onClick={editHandler}
          >
            <i className="fa fa-pencil"></i>
          </button>
        </div>
        {props.isSelected && (
          <div className="row">
            <span>{props.description}</span>
            <div className="col-2">
              <Moment format="YYYY/MM/DD">{props.date}</Moment>
            </div>
          </div>
        )}
      </div>
    </li>
  );
};
