import React, { useState } from 'react';
import Moment from 'react-moment';

export const AnnouncementListItem = (props) => {
  const [isEdit, setEdit] = useState(false);
  const [isSelect, setSelect] = useState(false);
  const [error, setError] = useState('');
  const [form, setForm] = useState({
    title: '',
    description: '',
  });

  const selectListItem = () => {
    setSelect(!isSelect);
  };

  const changeAnnouncementHandler = (event) => {
    setForm({ ...form, [event.target.name]: event.target.value });
    console.log(form);
  };

  const editAnnouncements = () => {
    setEdit(true);
  };

  const saveAnnouncements = () => {
    if (form.description === '' || form.title === '') {
      setError('enter all inputs');
      return;
    }
    const newAnnouncement = {
      ...form,
      date: new Date(),
    };
    props.changeAnnouncements(props.announcement.id, newAnnouncement);
    setError('');
    setForm({ title: '', description: '' });
  };

  const cancelAnnouncements = () => {
    setEdit(false);
  };

  const announcementSection = () => {
    if (!isEdit) {
      return (
        <div className="row">
          <div className="col-10">
            <span>{props.announcement.title}</span>
          </div>
          <div className="col-2 ">
            <button
              className="btn btn-outline-danger mr-1"
              type="button"
              onClick={() => props.deleteAnnouncements(props.announcement.id)}
            >
              <i className="fa fa-trash"></i>
            </button>
            <button
              className="btn btn-outline-warning"
              type="button"
              onClick={editAnnouncements}
            >
              <i className="fa fa-pencil"></i>
            </button>
          </div>
        </div>
      );
    }
    return (
      <div className="row">
        <div className="col-10">
          <input
            type="text"
            className="form-control"
            placeholder="ToDo title"
            name="title"
            value={form.title}
            onChange={changeAnnouncementHandler}
          />
          <input
            type="text"
            className="form-control"
            placeholder="ToDo description"
            name="description"
            value={form.description}
            onChange={changeAnnouncementHandler}
          />
        </div>
        {error}
        <div className="col-2 ">
          <button
            className="btn btn-outline-danger mr-1"
            type="button"
            onClick={cancelAnnouncements}
          >
            <i className="fa fa-ban"></i>
          </button>
          <button
            className="btn btn-outline-success"
            type="button"
            onClick={saveAnnouncements}
          >
            <i className="fa fa-check-circle"></i>
          </button>
        </div>
      </div>
    );
  };

  const selectedAnnouncementSection = () => {
    if (isSelect) {
      return (
        <div>
          {props.isSelected && (
            <div className="row">
              <span>{props.announcement.description}</span>
              <div className="col-2">
                <Moment format="YYYY/MM/DD">{props.announcement.date}</Moment>
              </div>
            </div>
          )}
        </div>
      );
    }
  };

  return (
    <li className="list-group-item mt-1" onClick={selectListItem}>
      <div className="row">
        <div className="col-10">
          {announcementSection()}
          {selectedAnnouncementSection()}
        </div>
      </div>
    </li>
  );
};
