import React, { useState } from 'react';
import { AnnouncementItem } from './AnnouncementItem';

export const Announcement = () => {
  const [form, setForm] = useState({ title: '', description: '' });
  const [editForm, setEditForm] = useState({ title: '', description: '' });
  const [announcements, setAnnouncement] = useState([
    {
      id: '1',
      title: 'titfegefgefgefgefel',
      description: 'description',
      date: new Date(),
      isSelected: false,
    },
  ]);
  const changeFormHandler = (event) => {
    setForm({ ...form, [event.target.name]: event.target.value });
  };
  const changeEditFormHandler = (event) => {
    setEditForm({ ...editForm, [event.target.name]: event.target.value });
  };
  const addAnnouncementHendler = () => {
    setAnnouncement([
      ...announcements,
      {
        id: Date.now(),
        title: form.title,
        description: form.description,
        date: new Date(),
        isSelected: false,
      },
    ]);
    setForm({ title: '', description: '' });
  };
  const deleteHandler = (id) => {
    setAnnouncement(
      announcements.filter((announcement) => announcement.id !== id)
    );
  };
  const saveEditHandler = (id) => {};
  const selectHandler = (id) => {
    // setAnnouncement(
    //   announcements.map((announcement) => {
    //     if (announcement.id === id) {
    //       announcement.isSelected = !announcement.isSelected;
    //     }
    //   })
    //);
    announcements.map((i) => i.id == id && console.log(i.isSelected));
  };
  return (
    <div className="container">
      <div className="row mt-5">
        <div className="col-5">
          <input
            type="text"
            className="form-control"
            placeholder="Announcement title"
            name="title"
            value={form.title}
            onChange={changeFormHandler}
          />
        </div>
        <div className="col-5">
          <input
            type="text"
            className="form-control"
            placeholder="Announcement description"
            name="description"
            value={form.description}
            onChange={changeFormHandler}
          />
        </div>
        <div className="col-2">
          <button
            type="button"
            className="btn btn-outline-primary float-right"
            onClick={addAnnouncementHendler}
          >
            Add announcement
          </button>
          <div />
        </div>
      </div>

      <div className="row mt-4">
        <div className="col">
          <ul className="list-group">
            {announcements.map((announcement) => {
              return (
                <AnnouncementItem
                  title={announcement.title}
                  date={announcement.date}
                  description={announcement.description}
                  id={announcement.id}
                  key={announcement.id}
                  isSelected={announcement.isSelected}
                  deleteHandler={deleteHandler}
                  saveEditHandler={saveEditHandler}
                  selectHandler={selectHandler}
                  changeFormHandler={changeEditFormHandler}
                />
              );
            })}
          </ul>
        </div>
      </div>
    </div>
  );
};
