import React, { useState } from 'react';
import { AnnouncementList } from './components/AnnouncementList';
import { CreateAnnouncement } from './components/CreateAnnouncement';

function App() {
  const [announcements, setAnnouncement] = useState([
    {
      id: '1',
      title: 'titfegefgefgefgefel',
      description: 'description',
      date: new Date(),
      isSelected: false,
    },
  ]);

  const addAnnouncements = (announcement) => {
    setAnnouncement([...announcements, announcement]);
    console.log(announcements);
  };

  const saveAnnouncements = () => {};
  const deleteAnnouncements = (id) => {
    setAnnouncement(
      announcements.filter((announcement) => announcement.id !== id)
    );
  };
  const changeAnnouncements = (id, newAnnouncement) => {
    setAnnouncement(
      announcements.map((announcement) => {
        if (announcement.id === id) {
          console.log('return', Object.assign(announcement, newAnnouncement));
          //Object.assign(announcement, newAnnouncement);
        }
      })
    );

    console.log(id);
    console.log(newAnnouncement);
  };

  return (
    <div className="container">
      <CreateAnnouncement
        announcements={announcements}
        addAnnouncements={addAnnouncements}
      />
      <AnnouncementList
        announcements={announcements}
        saveAnnouncements={saveAnnouncements}
        deleteAnnouncements={deleteAnnouncements}
        changeAnnouncements={changeAnnouncements}
      />
    </div>
  );
}

export default App;
